import React, { Component } from 'react'

//类式组件
/* export default class Demo extends Component {
    state = {count:0}
    increment  = () => {
        //函数式setState
        this.setState((state) => {
            
            return {count:state.count+1}

        })
        }
    render() {
        return (
            <div>
                <h1>当前求和为：{this.state.count} </h1>
                <button onClick ={this.increment} >点我加1</button>
            </div>
        )
    }
} */

