    render() {  
        return (
            <div>
                <h1>我是Person组件</h1>
                &nbsp;<Input type='text' style={{ width: 120 }} placeholder="输入名字" onChange={ this.addvalue} /><br/><br/>
                &nbsp;<Input type='text' style={{ width: 120 }} placeholder="输入年龄" onChange = {c => this.ageNode = c}  /><br/><br/>
                &nbsp;<Button type="primary" onClick ={this.addPerson} >添加</Button>
                <ul>
                    <li>名字1--年龄1</li>
                    <li>名字2--年龄2</li>
                    <li>名字3--年龄3</li>
                </ul>
            </div>
        )
    }
}
